SET NAMES UTF8;
DROP DATABASE IF EXISTS lmx;
CREATE DATABASE lmx CHARSET=UTF8;
USE lmx;
#创建文章分类表
CREATE TABLE lmx_word_family(
	f_id TINYINT PRIMARY KEY,
	f_name VARCHAR(8)
);
#创建文章表
CREATE TABLE lmx_word(
	w_id INT PRIMARY KEY AUTO_INCREMENT,
	w_read VARCHAR(8) DEFAULT '0',
	w_date VARCHAR(10),
	w_title VARCHAR(128),
	w_cont VARCHAR(8000),
	w_isAuthor TINYINT(1),
	family_id TINYINT,
	FOREIGN KEY(family_id) REFERENCES lmx_word_family(f_id)
);
#创建管理员表
CREATE TABLE lmx_admin(
	a_id TINYINT PRIMARY KEY,
	a_name VARCHAR(16),
	a_pwd VARCHAR(16)	
);
#创建留言信息表
CREATE TABLE lmx_msg(
	m_id INT PRIMARY KEY AUTO_INCREMENT,
	m_date VARCHAR(10),
	m_title VARCHAR(128),
	m_cont VARCHAR(1024),
	m_name VARCHAR(16),
	m_email VARCHAR(32)
);
